// --- Inicio: Funci√≥n para convertir JSON a CSV (MODIFICADA) ---
// (Esta funci√≥n no se usa para XLSX, solo para la rama CSV)
function jsonToCsv(data, includeHeaders = true) {
  if (!data || data.length === 0) {
    return "";
  }
  const dataArray = Array.isArray(data) ? data : [data];

  const sanitize = (value) => {
    if (value === null || value === undefined) {
      return '';
    }
    let str = String(value);
    if (str.includes(',') || str.includes('\n') || str.includes('"')) {
      return `"${str.replace(/"/g, '""')}"`;
    }
    return str;
  };

  const headers = Object.keys(dataArray[0]);
  const headerRow = headers.map(sanitize).join(',');

  const dataRows = dataArray.map(row => {
    return headers.map(header => {
      return sanitize(row[header]);
    }).join(',');
  });

  const rowsToJoin = [...dataRows];
  if (includeHeaders) {
    rowsToJoin.unshift(headerRow);
  }
  return rowsToJoin.join('\n');
}
