    const formatoRegex = /["']?formato_exportacion["']?\s*[:=]\s*["']?([a-zA-Z√°√©√≠√≥√∫√±√ë_]+)["']?/i;
    const formatoMatch = fullOutput.match(formatoRegex);
    if (formatoMatch) {
