    } else if (accion === 'descargar' || accion === 'enviar' || accion === 'drive') {
      formato = 'csv';
    }

    // 4. Buscar el JSON v√°lido
    let jsonString;
    const datosRegex = /["']?datos["']?\s*[:=]\s*(\[[\s\S]*?\]|\{[\s\S]*?\})/i;
    const datosMatch = fullOutput.match(datosRegex);

    if (datosMatch && datosMatch[1]) {
        jsonString = datosMatch[1];
    } else {
        let startIndex = fullOutput.indexOf('[');
        let endIndex = fullOutput.lastIndexOf(']');

        if (startIndex === -1 || endIndex === -1) {
            startIndex = fullOutput.indexOf('{');
            endIndex = fullOutput.lastIndexOf('}');
        }

        if (startIndex === -1 || endIndex === -1) {
            throw new Error("No se encontr√≥ JSON v√°lido.");
        }
        jsonString = fullOutput.substring(startIndex, endIndex + 1);
    }

    // 5. Parsear el JSON limpio
    const parsedJson = JSON.parse(jsonString.trim());
    // Convertir a array si es un objeto √∫nico
    const dataArray = Array.isArray(parsedJson) ? parsedJson : [parsedJson];


